//Class choice
void battlePick(){

	heroes();
	cout<< "\n";
	cout<< "We will also need to know what type of fighter you are, there are infinite possibilities of what you can be!\n";
	system("pause");
	system("cls");
	heroes();
	cout<< "\n";
	cout<< "....So here are 4 choices:\n";
	cout<< "1) Warrior\n2) Priest\n3) Thief\n4) Flying Bat Monkey\n";
	cin>> classChoice;

	if (classChoice == 1){
		warrior();
		cout<< "\n";
		cout << "A Warrior eh\? You probably want to punch things...\n\n";
		system("pause");
	}else if (classChoice == 2){
		priest();
		cout<< "\n";
		cout << "A Priest. Holy light and all that jazz?\n";
		system("pause");
	}else if (classChoice == 3){
		thief();
		cout<< "\n";
		cout << "A Thief. Stealthy and deadly. Much like the ovens of the Dutch.\n";
		system("pause");
	}else if (classChoice == 4){
		monkey();
		cout<< "\n";
		cout << "Really? Well... go for the eyes I guess?\n";
		system("pause");
	}else{
		cout << "Not a valid choice.\n";
		classChoice += 0;
		}
	return;

}

//Personality traits good value and bad value
void personalities(){ // @suppress("No return")

	system("cls");
	dice();
	cout << "Very well, now i'm going to probe you... for information\n";
	system("pause");
	system("cls");
	cout << "Say you find a Turtle on the ground. It appears to have jumped out of it's bowl.\n";
	cout << "Do you: \n\n";
	cout << "1) Wash it off and put it back in the fish tank?\n\nOr\n\n2) Pick it up and put it back in the soup you were eating?\n";
	cin >> p1;
	if (p1 == 1){
		cout << "Hah, jokes on you good guy. It was a Tortoise!. Next Question!\n";
		good++;
	}else if (p1 == 2){
		cout << "Ah, I too shall dine on Turtle soup. Ahem, Next question!\n";
		bad++;
	}else{
		cout << "Not a valid choice.\n";
		classChoice += 0;
	}
	system("pause");
	system("cls");
	dice();
	cout << "You are walking down the street and you see a guy getting mugged. What do you do?\n\n";
	cout << "1) Rush to his aid while calling the authorities?\n\nOr\n\n2) Tag yourself in and get that $5 he owes you too!\n";
	cin >> p2;
	if (p2 == 1){
		cout << "Quite Heroic. Dumb...but Heroic.\n";
		good++;
	}else if (p2 == 2){
		cout << "Dang... remind me to never borrow money from you...\n";
		bad++;
	}else{
		cout << "Not a valid choice.\n";
		classChoice += 0;
	}
	system("pause");
	system("cls");
	dice();
	cout << "Ok last one, I promise. Unless you want more?\n";
	cout << "1) Yes    2) No\n";
	cin >> p3;
	if (p3 == 1){
		cout << "You are a sick, sick person...I like it. Let's begin.\n";
		good++;
	}else if (p3 == 2){
		cout << "Fine... let us begin\n";
		bad++;
	}else{
		cout << "Not a valid choice.\n";
		classChoice += 0;
	}
	return;
	system("pause");
	system("cls");
	}


